template.code:
var
  ref: T|BaseName|View;
begin
  inherited;
  if not assigned(FView) then
  begin
    Application.CreateForm(T|BaseName|View, ref);
    supports(ref, IView, FView);
{$IFDEF FMX}
    if Application.MainForm = nil then
      Application.RealCreateForms;
{$ENDIF}
  end;
  // CreateModules; // < criar os modulos persolnizados

  AfterInit;
end;
