template.code:
 ///Usando Criteria
 function T|class|.Get|table|(AWhere:string):IDBResultSet;
 begin
      result := TCriteria.New
        .SetConnection(FConnection)
        .SQL(CreateCriteria
        .Select
        .All
        .From('|table|')
        .Where(AWhere)
        .AsString)
        .AsResultSet;
end;
