template.code:
 /// Validacao usando Criteria
 function T|class|.Validate|name|(AValue:|paramType|):boolean;
 var LValidarSet:IDBResultSet;
 begin
      LValidarSet := TCriteria.New
        .SetConnection(FConnection)
        .SQL(CreateCriteria
        .Select
        .All
        .From('|table|')
        .Where('|where|')
        .First(1)
        .AsString)
        .AsResultSet;
     result := LValidarSet.RecordCount>0;
end;
